mapper.insert:

  SELECT INQUIRY_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO INQUIRY(
    INQUIRY_NO
  , USER_NO
  , INQUIRY_TITLE
  , INQUIRY_CONTENT
  , INQUIRY_CREATED_AT
) VALUES (
    #{inquiryNo}
  , #{userDto.userNo}
  , #{inquiryTitle}
  , #{inquiryContent}
  , SYSTIMESTAMP
)

mapper.select:
SELECT COUNT(*)
  FROM ANSWER
 WHERE INQUIRY_NO = #{inquiryNo}
