mapper.select:
SELECT USER_NO, EMAIL, NAME, PW, GENDER, PHONE, AGREE, STATE, JOINED_AT, SIDO, SIGUNGU, INTEREST_CITY
  FROM USER_T

  EMAIL = #{email}
  AND PW = #{pw}
  AND USER_NO = #{userNo}

mapper.update:
UPDATE USER_T
   SET PW = #{pw}
 WHERE USER_NO = #{userNo}

UPDATE USER_T
   SET NAME = #{name}
     , GENDER = #{gender}
     , PHONE = #{phone}
     , AGREE = #{agree}
     , SIDO = #{sido}
     , SIGUNGU = #{sigungu}
 WHERE USER_NO = #{userNo}
