mapper.insert:


  SELECT UPLOAD_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO UPLOAD_T (
    UPLOAD_NO
  , TITLE
  , CONTENTS
  , USER_NO
  , CREATED_AT
  , MODIFIED_AT
) VALUES (
    #{uploadNo}
  , #{title}
  , #{contents}
  , #{userDto.userNo}
  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
)

INSERT INTO ATTACH_T (
    ATTACH_NO
  , PATH
  , ORIGINAL_FILENAME
  , FILESYSTEM_NAME
  , DOWNLOAD_COUNT
  , HAS_THUMBNAIL
  , UPLOAD_NO
) VALUES (
    ATTACH_SEQ.NEXTVAL
  , #{path}
  , #{originalFilename}
  , #{filesystemName}
  , 0
  , #{hasThumbnail}
  , #{uploadNo}
)

mapper.select:
SELECT ATTACH_NO, PATH, ORIGINAL_FILENAME, FILESYSTEM_NAME, DOWNLOAD_COUNT, HAS_THUMBNAIL, UPLOAD_NO
  FROM ATTACH_T
 WHERE UPLOAD_NO = #{uploadNo}

SELECT ATTACH_NO, PATH, ORIGINAL_FILENAME, FILESYSTEM_NAME, DOWNLOAD_COUNT, HAS_THUMBNAIL, UPLOAD_NO
  FROM ATTACH_T
 WHERE ATTACH_NO = #{attachNo}

mapper.update:
UPDATE ATTACH_T
   SET DOWNLOAD_COUNT = DOWNLOAD_COUNT + 1
 WHERE ATTACH_NO = #{attachNo}
