mapper.select:
SELECT USER_NO, EMAIL, NAME, PW, GENDER, PHONE, AGREE, STATE, JOINED_AT, SIDO, SIGUNGU, INTEREST_CITY
  FROM USER_T

  EMAIL = #{email}
  AND PW = #{pw}
  AND USER_NO = #{userNo}

SELECT EMAIL, JOINED_AT, LEAVED_AT
  FROM LEAVE_USER
 WHERE EMAIL = #{email}

SELECT USER_NO, EMAIL, NAME, PW, GENDER, PHONE, AGREE, STATE, JOINED_AT, INACTIVED_AT, SIDO, SIGUNGU, INTEREST_CITY
  FROM INACTIVE_USER

  EMAIL = #{email}
  AND PW = #{pw}

mapper.insert:
INSERT INTO USER_T (
    USER_NO
  , EMAIL
  , NAME
  , PW
  , GENDER
  , PHONE
  , AGREE
  , STATE
  , JOINED_AT
  , SIDO
  , SIGUNGU
  , INTEREST_CITY
) VALUES (
    USER_SEQ.NEXTVAL
  , #{email}
  , #{name}
  , #{pw}
  , #{gender}
  , #{phone}
  , #{agree}
  , 0
  , SYSTIMESTAMP
  , #{sido}
  , #{sigungu}
  , #{interestCity}
)

INSERT INTO ACCESS_T (
    EMAIL
  , LOGIN_AT
) VALUES (
    #{email}
  , SYSTIMESTAMP
)
